CREATE OR REPLACE PROCEDURE grava_justificativa_diverg ( cd_tipo_divergencia_p bigint, nr_seq_nf_p bigint, nr_seq_tipo_justificativa_p bigint, ds_justificativa_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w			bigint;
nr_item_doi_nfe_w			integer;
nr_ordem_compra_w		bigint;

c01 CURSOR FOR
SELECT	nr_sequencia,
	nr_ordem_compra,
	nr_item_doi_nfe
from	divergencia_ordem_compra_item
where	cd_tipo_divergencia = cd_tipo_divergencia_p
and	nr_seq_nf = nr_seq_nf_p;


BEGIN

open C01;
loop
fetch C01 into
	nr_sequencia_w,
	nr_ordem_compra_w,
	nr_item_doi_nfe_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	insert into divergencia_oci_justif(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_ordem_compra,
		nr_item_doi_nfe,
		cd_tipo_divergencia,
		nr_seq_tipo_justificativa,
		ds_justificativa,
		nr_seq_oci_just)
	values (nextval('divergencia_oci_justif_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_ordem_compra_w,
		nr_item_doi_nfe_w,
		cd_tipo_divergencia_p,
		CASE WHEN nr_seq_tipo_justificativa_p=0 THEN ''  ELSE nr_seq_tipo_justificativa_p END ,
		ds_justificativa_p,
		nr_sequencia_w);
	end;
end loop;
close C01;

commit;

end;
$body$
