CREATE OR REPLACE PROCEDURE grava_log_edicao_img (ds_usuario_p text, nr_seq_capturas_p text) AS $body$
WITH RECURSIVE cte AS (
DECLARE

--cursosr para retornar os valores separados por virgula
C01 is CURSOR
with data as (SELECT nr_seq_capturas_p str )
SELECT trim(both regexp_substr(str, '[^,]+', 1, 1)) nr_seq_captura_w
from data
instr(str, ',', 1, level - 1) > 0  UNION ALL
DECLARE


C01 is CURSOR
with data as (SELECT nr_seq_capturas_p str )
SELECT trim(both regexp_substr(str, '[^,]+', 1, (c.level+1))) nr_seq_captura_w
from data
instr(str, ',', 1, level - 1) > 0 JOIN cte c ON ()

) SELECT * FROM cte;
;

linha_w			c01%rowtype;


BEGIN

open C01;
loop
fetch C01 into
	linha_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
  begin
    insert into captura_imagem_edicao_log
    values (nextval('captura_imagem_edicao_log_seq'),
            clock_timestamp(),
            ds_usuario_p,
            linha_w.nr_seq_captura_w);
  end;
end loop;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
