CREATE OR REPLACE PROCEDURE grava_observacao_man_equip ( nr_sequencia_p bigint, ds_observacao_p text) AS $body$
DECLARE


ds_observacao_w				varchar(2000);


BEGIN

select	ds_observacao
into STRICT	ds_observacao_w
from	man_equipamento
where	nr_sequencia = nr_sequencia_p;

if (ds_observacao_w IS NOT NULL AND ds_observacao_w::text <> '') then
	ds_observacao_w := ds_observacao_w || chr(13) || chr(10) || ds_observacao_p;
else
	ds_observacao_w := ds_observacao_p;
end if;

update	man_equipamento
set	ds_observacao = ds_observacao_w
where	nr_sequencia = nr_sequencia_p;

commit;

end;
$body$
