CREATE OR REPLACE PROCEDURE grava_protocolo_descricao_uso ( cd_protocolo_p bigint, nr_seq_protocolo_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

nr_seq	bigint;

BEGIN

select 	max(nr_sequencia)
into STRICT 	nr_seq
from	protocolo_descricao_uso
where	cd_estabelecimento 	= cd_estabelecimento_p
and	cd_protocolo 		= cd_protocolo_p
and	nr_seq_protocolo_desc	= nr_seq_protocolo_p
and	nm_usuario 		= nm_usuario_p;

if (coalesce(nr_seq,0) > 0) then
	update 	protocolo_descricao_uso
	set	dt_atualizacao 	= clock_timestamp(),
		qt_utilizado 	= qt_utilizado + 1
	where	nr_sequencia	= nr_seq;
else
	insert into protocolo_descricao_uso(nr_sequencia,
		cd_estabelecimento,
		cd_protocolo,
		nr_seq_protocolo_desc,
		nm_usuario,
		dt_atualizacao,
		qt_utilizado)
	values (nextval('protocolo_descricao_uso_seq'),
		cd_estabelecimento_p,
		cd_protocolo_p,
		nr_seq_protocolo_p,
		nm_usuario_p,
		clock_timestamp(),
		1);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
