CREATE OR REPLACE PROCEDURE grava_status_sms_envio_pac (dt_parametro_p timestamp) AS $body$
DECLARE


C01 CURSOR FOR
	SELECT	nr_sequencia,
		dt_ciencia,
		nm_usuario,
		id_sms
	from	ev_evento_pac_destino
	where	dt_ciencia	> dt_parametro_p - 5
	and	(id_sms IS NOT NULL AND id_sms::text <> '')
	and (coalesce(ie_status_sms::text, '') = '' or ie_status_sms = '180')
	order by nr_sequencia;


nr_sequencia_w			bigint;
dt_ciencia_w			timestamp;
nm_usuario_w			varchar(20);
id_sms_w			bigint;
ie_status_sms_w			varchar(20);


BEGIN

open C01;
loop
fetch C01 into
	nr_sequencia_w,
	dt_ciencia_w,
	nm_usuario_w,
	id_sms_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	substr(wheb_sms.OBTER_STATUS_SMS(id_sms_w, dt_ciencia_w, nm_usuario_w),1,3)
	into STRICT	ie_status_sms_w
	;

	if (ie_status_sms_w IS NOT NULL AND ie_status_sms_w::text <> '') then
		update	ev_evento_pac_destino
		set	ie_status_sms		= ie_status_sms_w
		where	nr_sequencia		= nr_sequencia_w;

	end if;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
