CREATE OR REPLACE PROCEDURE grava_usuario_conectado (ds_endereco_ip_p text, cd_funcao_p text, nm_usuario_p text, nm_usuario_con_p text, ds_maquina_p text, dt_fim_conexao_p timestamp, nr_porta_p INOUT text) AS $body$
BEGIN

update	usuario_conectado
set	dt_fim_conexao	= clock_timestamp()
where 	dt_inicio_conexao 	< (clock_timestamp() - interval '1 days')
and	coalesce(dt_fim_conexao::text, '') = ''
and	nm_usuario_con	= nm_usuario_con_p;

update	usuario_conectado
set	dt_fim_conexao	= clock_timestamp()
where	coalesce(dt_fim_conexao::text, '') = ''
and	ds_endereco_ip	= ds_endereco_ip_p
and	nm_usuario_con	= nm_usuario_con_p;

if (coalesce(dt_fim_conexao_p::text, '') = '') then
	select	to_char(coalesce(max((nr_porta)::numeric ),'0'))
	into STRICT	nr_porta_p
	from	usuario_conectado
	where	coalesce(dt_fim_conexao::text, '') = ''
	  and	ds_endereco_ip	= ds_endereco_ip_p
	  and	nm_usuario_con	= nm_usuario_con_p;

	if (nr_porta_p = '0') then
		select	to_char(CASE WHEN coalesce(max((nr_porta)::numeric ),0)=0 THEN 100  ELSE max((nr_porta)::numeric ) END  + 2)
		into STRICT	nr_porta_p
		from	usuario_conectado
		where	coalesce(dt_fim_conexao::text, '') = ''
		and	ds_endereco_ip	= ds_endereco_ip_p;
	end if;

	insert into
	usuario_conectado(nr_sequencia,
				nm_usuario_con,
				cd_funcao,
				dt_inicio_conexao,
				dt_atualizacao,
				nm_usuario,
				ds_endereco_ip,
				ds_maquina,
				nr_porta)
	values (nextval('usuario_conectado_seq'),
				nm_usuario_con_p,
				cd_funcao_p,
				clock_timestamp(),
				clock_timestamp(),
				nm_usuario_p,
				ds_endereco_ip_p,
				ds_maquina_p,
				nr_porta_p);
else
	update usuario_conectado
	set	dt_fim_conexao = dt_fim_conexao_p
	where	nm_usuario_con = nm_usuario_con_p
	and	ds_endereco_ip = ds_endereco_ip_p
	and	coalesce(dt_fim_conexao::text, '') = '';
end if;
commit;
end;
$body$
