CREATE OR REPLACE PROCEDURE grava_xml_consulta_res ( nr_atendimento_p text, nm_usuario_p text, ds_xml_p text) AS $body$
DECLARE



nr_seq_sumario_w			varchar(20);
cd_pessoa_fisica_w			varchar(100);


BEGIN
select max(cd_pessoa_fisica)
into STRICT cd_pessoa_fisica_w
from atendimento_paciente
where nr_atendimento = nr_atendimento_P;

select max(nr_sequencia)
into STRICT nr_seq_sumario_w
from IMP_SUMARIO_PACIENTE
where cd_pessoa_fisica = cd_pessoa_fisica_w
and nm_usuario = nm_usuario_p;

if (ds_xml_p IS NOT NULL AND ds_xml_p::text <> '') and (nr_seq_sumario_w IS NOT NULL AND nr_seq_sumario_w::text <> '') then


INSERT INTO IMP_SUMARIO_PAC_ARQUIVO(nr_sequencia,
								   dt_atualizacao,
								   nm_usuario,
								   dt_atualizacao_nrec,
								   nm_usuario_nrec,
								   nr_seq_sumario,
								   ie_tipo_arquivo,
								   ds_conteudo) VALUES ( nextval('imp_sumario_pac_arquivo_seq'),
								   clock_timestamp(),
								   nm_usuario_p,
								   clock_timestamp(),
								   nm_usuario_p,
								   nr_seq_sumario_w,
								   'XML',
								   ds_xml_p);

end if;

commit;

end;
$body$
