CREATE OR REPLACE PROCEDURE grg_marcar_guia_item_pago (nr_seq_guia_p bigint, nm_usuario_p text, ie_opcao_p text, ie_pago_p text) AS $body$
BEGIN

if ( ie_opcao_p = 'T') then
    update	lote_audit_hist_item
    set	ie_pago_convenio	= ie_pago_p,
        nm_usuario		= nm_usuario_p,
        dt_atualizacao		= clock_timestamp()
    where	nr_seq_guia		= nr_seq_guia_p;

elsif (ie_opcao_p = 'R') then
    update	 lote_audit_hist_item
    set	ie_pago_convenio	= ie_pago_p,
        nm_usuario		= nm_usuario_p,
        dt_atualizacao		= clock_timestamp()
    where	nr_seq_guia		= nr_seq_guia_p
    and vl_amenor > 0;

end if;
commit;

end;
$body$
