SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gv_obter_se_atendimento_aberto (cd_pessoa_fisica_p text, ds_mensagem_p INOUT text) AS $body$
DECLARE

 
nr_atendimento_w	bigint;					
ds_mensagem_w		varchar(2000);
ds_tipo_atendimento_w	varchar(255);
dt_entrada_w		timestamp;
ds_setor_atendimento_w	varchar(255);


BEGIN 
ds_mensagem_p := null;
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then 
	select	max(nr_atendimento) 
	into STRICT	nr_atendimento_w 
	from	atendimento_paciente 
	where 	cd_pessoa_fisica = cd_pessoa_fisica_p 
	and 	coalesce(dt_cancelamento::text, '') = '' 
	and 	coalesce(dt_alta::text, '') = '';
 
	if (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '') and (nr_atendimento_w > 0) then 
		select	max(substr(obter_nome_tipo_atend(IE_TIPO_ATENDIMENTO),1,30)), 
			max(dt_entrada), 
			max(substr(OBTER_NOME_SETOR(Obter_Setor_Atendimento(nr_atendimento)),1,100)) 
		into STRICT	ds_tipo_atendimento_w, 
			dt_entrada_w, 
			ds_setor_atendimento_w 
		from	atendimento_paciente 
		where	nr_atendimento = nr_atendimento_w;
		 
		ds_mensagem_w	:= obter_texto_tasy(197820,null);
		ds_mensagem_w	:= Tiss_Substituir_String(ds_mensagem_w,'#@NR_ATENDIMENTO#@',nr_atendimento_w);
		ds_mensagem_w	:= Tiss_Substituir_String(ds_mensagem_w,'#@DS_TIPO_ATEND#@',ds_tipo_atendimento_w);
		ds_mensagem_w	:= Tiss_Substituir_String(ds_mensagem_w,'#@DT_ENTRADA#@',to_char(dt_entrada_w,'dd/mm/yyyy hh24:mi:ss'));
		ds_mensagem_w	:= Tiss_Substituir_String(ds_mensagem_w,'#@DS_SETOR#@',ds_setor_atendimento_w);
		 
		ds_mensagem_p := ds_mensagem_w;
		 
	end if;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gv_obter_se_atendimento_aberto (cd_pessoa_fisica_p text, ds_mensagem_p INOUT text) FROM PUBLIC;

