CREATE OR REPLACE PROCEDURE gv_obter_se_campo_preenchido ( ie_tipo_alta_p text, it_tipo_status_alta_p text, nm_usuario_p text, DT_PREVISTO_ALTA_p text, CD_SETOR_DESEJADO_p text, IE_TIPO_VAGA_p text, DS_OBS_PREV_ALTA_p text, cd_tipo_acomod_desej_p text, ds_erro_p INOUT text) AS $body$
DECLARE

ie_data_prevista_w	varchar(1) := 'N';
ie_setor_desej_w	varchar(1) := 'N';
ie_tipo_vaga_w		varchar(1) := 'N';
ie_obervacao_w		varchar(1) := 'N';
ie_tipo_acomod_desej_w	varchar(1) := 'N';
nr_seq_regra_w		bigint;
ds_erro_w		varchar(255) := wheb_mensagem_pck.get_texto(280535,null);
ds_erro_dt_w		varchar(200);
ds_erro_sd_w		varchar(200);
ds_erro_tv_w		varchar(200);
ds_erro_bs_w		varchar(200);
ds_erro_ac_w		varchar(200);

BEGIN
if (it_tipo_status_alta_p IS NOT NULL AND it_tipo_status_alta_p::text <> '') and (ie_tipo_alta_p IS NOT NULL AND ie_tipo_alta_p::text <> '') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_regra_w
	from	regra_campo_obr_prob_alta
	where	ie_tipo_alta	    = ie_tipo_alta_p
	and	it_tipo_status_alta = it_tipo_status_alta_p
	and	ie_situacao = 'A';

	if (coalesce(nr_seq_regra_w,0) > 0) then

		select	coalesce(max(ie_data_prevista)	,'N'),
			coalesce(max(ie_setor_desej)  	,'N'),
			coalesce(max(ie_tipo_vaga)    	,'N'),
			coalesce(max(ie_obervacao)    	,'N'),
			coalesce(max(ie_tipo_acomod_desej)	,'N')
		into STRICT	ie_data_prevista_w,
			ie_setor_desej_w,
			ie_tipo_vaga_w,
			ie_obervacao_w,
			ie_tipo_acomod_desej_w
		from	regra_campo_obr_prob_alta
		where	nr_sequencia = nr_seq_regra_w;

		if ((coalesce(DT_PREVISTO_ALTA_p::text, '') = '') or (DT_PREVISTO_ALTA_p = '')) and (ie_data_prevista_w = 'S') then
			ds_erro_dt_w := WHEB_MENSAGEM_PCK.get_texto(280543,null);
		end if;

		if (coalesce(CD_SETOR_DESEJADO_p::text, '') = '') and (ie_setor_desej_w = 'S') then
			if (coalesce(ds_erro_dt_w::text, '') = '') then
				ds_erro_sd_w := WHEB_MENSAGEM_PCK.get_texto(280544,null);
			else
				ds_erro_sd_w := WHEB_MENSAGEM_PCK.get_texto(280547,null);
			end if;
		end if;

		if (coalesce(IE_TIPO_VAGA_p::text, '') = '') and (ie_tipo_vaga_w = 'S') then
			if (coalesce(ds_erro_dt_w::text, '') = '') and (coalesce(ds_erro_sd_w::text, '') = '') then
				ds_erro_tv_w := WHEB_MENSAGEM_PCK.get_texto(280548,null);
			else
				ds_erro_tv_w := WHEB_MENSAGEM_PCK.get_texto(280549,null);
			end if;
		end if;

		if (coalesce(DS_OBS_PREV_ALTA_p::text, '') = '') and (ie_obervacao_w = 'S') then
			if (coalesce(ds_erro_dt_w::text, '') = '') and (coalesce(ds_erro_sd_w::text, '') = '') and (coalesce(ds_erro_tv_w::text, '') = '') then
				ds_erro_bs_w := WHEB_MENSAGEM_PCK.get_texto(280550,null);
			else
				ds_erro_bs_w := WHEB_MENSAGEM_PCK.get_texto(280551,null);
			end if;
		end if;

		if (coalesce(cd_tipo_acomod_desej_p::text, '') = '') and (ie_tipo_acomod_desej_w = 'S') then
			if (coalesce(ds_erro_dt_w::text, '') = '') and (coalesce(ds_erro_sd_w::text, '') = '') and (coalesce(ds_erro_tv_w::text, '') = '') and (coalesce(ds_erro_bs_w::text, '') = '') then
				ds_erro_ac_w := WHEB_MENSAGEM_PCK.get_texto(280552,null);
			else
				ds_erro_ac_w := WHEB_MENSAGEM_PCK.get_texto(280553,null);
			end if;
		end if;

	end if;
end if;
if (coalesce(ds_erro_dt_w::text, '') = '') and (coalesce(ds_erro_sd_w::text, '') = '') and (coalesce(ds_erro_tv_w::text, '') = '') and (coalesce(ds_erro_bs_w::text, '') = '') and (coalesce(ds_erro_ac_w::text, '') = '') then
	ds_erro_p := '';
else
	ds_erro_p := ds_erro_w||ds_erro_dt_w||ds_erro_sd_w||ds_erro_tv_w||ds_erro_bs_w||ds_erro_ac_w;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
