CREATE OR REPLACE PROCEDURE hcp_update_batch (nr_seq_batch_p bigint, nm_usuario_p text) AS $body$
BEGIN
    UPDATE hcp_batch_sending 
    SET    dt_sending = clock_timestamp(), 
           dt_atualizacao = clock_timestamp(), 
           nm_usuario = nm_usuario_p, 
           ie_sending_status = 'E' 
    WHERE  nr_sequencia = nr_seq_batch_p;

    COMMIT;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
