SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE hc_agenda_hc_controle ( nr_seq_agenda_p bigint, ds_operacao_p text, nm_usuario_p text) AS $body$
DECLARE


/*
Valores para ds_operacao_p:
E - Exclusão
I - Inclusão
*/
BEGIN

if (ds_operacao_p IS NOT NULL AND ds_operacao_p::text <> '') then

	if (ds_operacao_p = 'E') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then

		delete
		from	agenda_hc_controle
		where	nm_usuario = nm_usuario_p
		and (nr_seq_agenda = nr_seq_agenda_p
			or	coalesce(nr_seq_agenda_p::text, '') = '');

	elsif (ds_operacao_p = 'I') and (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') and (nr_seq_agenda_p > 0) then

		insert into agenda_hc_controle(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_agenda
		) values (
			nextval('agenda_hc_controle_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_agenda_p
		);

	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
