CREATE OR REPLACE PROCEDURE hc_alterar_recomendacao ( ds_recomendacao_p text, nr_seq_agenda_p bigint, nm_usuario_p text) AS $body$
BEGIN

if (ds_recomendacao_p IS NOT NULL AND ds_recomendacao_p::text <> '') and (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then

	update	agenda_hc_paciente
	set   	ds_observacao = ds_recomendacao_p,
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where	nr_sequencia = nr_seq_agenda_p;

end if;

commit;

end;
