CREATE OR REPLACE PROCEDURE hc_cadastrar_prof_agenda ( cd_pessoa_fisica_p text, nr_seq_agenda_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


ie_existe_w	varchar(1);
nr_sequencia_w	bigint;

C01 CURSOR FOR
SELECT 	nr_sequencia cd
from 	AGENDA_HOME_CARE
where 	ie_situacao = 'A'
and 	cd_estabelecimento = cd_estabelecimento_p
and 	obter_se_contido(nr_sequencia,nr_seq_agenda_p) = 'S';


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT 	ie_existe_w
	from 	AGENDA_HOME_CARE_EQUIPE
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p
	and		nr_seq_agenda		= nr_sequencia_w;

	if (ie_existe_w = 'N')	 then
		insert into AGENDA_HOME_CARE_EQUIPE(
						NR_SEQUENCIA,
						DT_ATUALIZACAO,
						NM_USUARIO,
						NR_SEQ_AGENDA,
						CD_PESSOA_FISICA)
		values (
						nextval('agenda_home_care_equipe_seq'),
						clock_timestamp(),
						nm_usuario_p,
						nr_sequencia_w,
						cd_pessoa_fisica_p);
	end if;
	end;
end loop;
close C01;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
