CREATE OR REPLACE PROCEDURE hc_cancelar_agendamento_reag ( cd_pessoa_fisica_p text, ie_Acao_p text, nm_usuario_p text, dt_inicial_p timestamp, dt_final_p timestamp) AS $body$
BEGIN

	update	agenda_hc_paciente
	set	ie_status_agenda	= ie_acao_p,
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p
	and	dt_agenda between dt_inicial_p and dt_final_p;

commit;

end;
$body$
