CREATE OR REPLACE PROCEDURE hc_cancelar_home_care ( NR_SEQUENCIA_P bigint, NR_SEQ_MOTIVO_CANC_P bigint, CD_PESSOA_FISICA_P bigint, NM_USUARIO_P text ) AS $body$
BEGIN
	if (NR_SEQUENCIA_P IS NOT NULL AND NR_SEQUENCIA_P::text <> '' AND NR_SEQ_MOTIVO_CANC_P IS NOT NULL AND NR_SEQ_MOTIVO_CANC_P::text <> '') then
		begin
			update PACIENTE_HOME_CARE
			set DT_CANCELAMENTO = clock_timestamp(), 
			NM_USUARIO_CANC = NM_USUARIO_P, 
			NR_SEQ_MOTIVO_CANC = NR_SEQ_MOTIVO_CANC_P, 
			DT_FINAL = clock_timestamp()
			where nr_sequencia = NR_SEQUENCIA_P;

			CALL CANC_AGEND_FUTUROS(CD_PESSOA_FISICA_P, NR_SEQ_MOTIVO_CANC_P);

		end;
	end if;

END;
$body$
