SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE hc_consiste_hor_agenda (nr_seq_agenda_p bigint, dt_agenda_p timestamp, nr_sequencia_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE


ie_agenda_hor_w	varchar(1);
ds_erro_w	varchar(255);
ie_agendar_w	varchar(1);


BEGIN
ds_erro_w := '';
if (coalesce(nr_seq_agenda_p,0) > 0) then

	SELECT	hc_consulta_bloqueio(nr_seq_agenda_p, dt_agenda_p, 'M')
	into STRICT	ds_erro_w
	;

	if (coalesce(ds_erro_w,'X') = 'X') then

		select	ie_agenda_hor
		into STRICT	ie_agenda_hor_w
		from	agenda_home_care
		where	nr_sequencia = nr_seq_agenda_p;

		if (coalesce(ie_agenda_hor_w,'S') = 'N') then

			select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
			into STRICT	ie_agendar_w
			from	agenda_hc_paciente
			where	dt_agenda = dt_agenda_p
			and	nr_sequencia <> nr_sequencia_p;

			if (ie_agendar_w = 'N') then
				ds_erro_w	:= WHEB_MENSAGEM_PCK.get_texto(281238,null);
			end if;
		end if;
	else
		ds_erro_w := WHEB_MENSAGEM_PCK.get_texto(281239,null) || ds_erro_w;
	end if;
end if;

ds_erro_p	:= ds_erro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
