CREATE OR REPLACE PROCEDURE hc_consistir_gerar_parecer ( cd_pessoa_fisica_p text, cd_estabelecimento_p bigint, ie_bloqueia_p INOUT text) AS $body$
DECLARE

					 
ie_exige_aval_parecer_w	varchar(1);
					

BEGIN 
 
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then 
 
	select	coalesce(max(ie_exige_aval_parecer),'N') 
	into STRICT	ie_exige_aval_parecer_w 
	from	hc_parametro 
	where	cd_estabelecimento = cd_estabelecimento_p;
	 
	if ( ie_exige_aval_parecer_w = 'S') then 
		begin 
		select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END  
		into STRICT	ie_bloqueia_p 
		from	atendimento_checklist a, 
			checklist_processo_regra b, 
			checklist_processo c, 
			med_avaliacao_paciente d 
		where	b.nr_seq_checklist	= c.nr_sequencia 
		and	a.nr_seq_checklist	= c.nr_sequencia 
		AND	a.nr_sequencia		= d.nr_seq_atend_checklist 
		AND	(d.dt_liberacao IS NOT NULL AND d.dt_liberacao::text <> '') 
		and	a.cd_pessoa_fisica	= cd_pessoa_fisica_p 
		and	b.ie_momento		= 'P';
		end;
	else 
		ie_bloqueia_p := 'N';
	end if;
end if;
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
