CREATE OR REPLACE PROCEDURE hc_consistir_periodo_visita ( cd_pessoa_fisica_p text, dt_agenda_p timestamp, cd_estabelecimento_p bigint, nr_seq_agenda_p bigint default null, ie_bloqueia_p INOUT text DEFAULT NULL) AS $body$
DECLARE


cd_intervalo_w		bigint;
cd_pessoa_fisica_w	varchar(15);


BEGIN
if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then
	begin
	select	max(cd_pessoa_fisica)
	into STRICT	cd_pessoa_fisica_w
	from	agenda_hc_paciente
	where	nr_sequencia = nr_seq_agenda_p;
	end;
else
	cd_pessoa_fisica_w := cd_pessoa_fisica_p;
end if;

if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then

	select	max(cd_intervalo_visita)
	into STRICT	cd_intervalo_w
	from	hc_parametro
	where	cd_estabelecimento = cd_estabelecimento_p;

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_bloqueia_p
	from	agenda_hc_paciente a
	where	a.cd_pessoa_fisica = cd_pessoa_fisica_w
	and	((cd_intervalo_w < (clock_timestamp() - a.dt_agenda)::numeric ) and (cd_intervalo_w > (dt_agenda_p - a.dt_agenda)::numeric ))
	and	a.dt_agenda = (	SELECT	max(b.dt_agenda)
				from	agenda_hc_paciente b
				where	b.cd_pessoa_fisica = cd_pessoa_fisica_w);

end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
