CREATE OR REPLACE PROCEDURE hc_gerar_propaci_equipamentos (nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_pac_equip_w	bigint;
nr_seq_equipamento_w	bigint;
nr_seq_paciente_w	bigint;
nr_atendimento_w	bigint;
cd_pessoa_fisica_w	varchar(10);

C01 CURSOR FOR 
	SELECT	a.nr_sequencia, 
		a.nr_seq_equipamento, 
		a.nr_seq_paciente 
	from	hc_pac_equipamento a 
	where	clock_timestamp() between dt_inicio_utilizacao and coalesce(dt_fim_utilizacao,clock_timestamp()) 
	order by 1;
			

BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_seq_pac_equip_w, 
	nr_seq_equipamento_w, 
	nr_seq_paciente_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	select	max(nr_atendimento_origem), 
		max(cd_pessoa_fisica) 
	into STRICT	nr_atendimento_w, 
		cd_pessoa_fisica_w 
	from	paciente_home_care 
	where	nr_sequencia = nr_seq_paciente_w;
	 
	if (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '') then 
		begin 
		CALL Gerar_propaci_equip_HC(nr_atendimento_w, nr_seq_equipamento_w, nr_seq_pac_equip_w, nm_usuario_p);
		exception 
		when others then 
			CALL gravar_log_tasy(55485, wheb_mensagem_pck.get_texto(802489, 
									'NM_PESSOA_FISICA='||obter_nome_pf(cd_pessoa_fisica_w)|| 
									';DS_ERRO='||sqlerrm), nm_usuario_p);
		end;
	else 
		CALL gravar_log_tasy(55485, wheb_mensagem_pck.get_texto(802492, 'NM_PESSOA_FISICA='||obter_nome_pf(cd_pessoa_fisica_w)), nm_usuario_p);
	end if;
	 
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
