CREATE OR REPLACE PROCEDURE hc_gerar_solic_pront_agenda (cd_pessoa_fisica_p text, nr_seq_agenda_p bigint, nr_sequencia_p bigint, dt_agenda_p timestamp, nm_usuario_p text) AS $body$
DECLARE


nr_prontuario_w		bigint;
nr_seq_prontuario_w	bigint;
nr_seq_solic_w		bigint;
qt_existe_cpi_w		bigint;
nr_sequencia_w		bigint;
cd_setor_atendimento_w 	integer;
cd_estabelecimento_w	integer;
nm_usuario_w		varchar(15);


BEGIN

select	coalesce(max(nr_prontuario), 0)
into STRICT	nr_prontuario_w
from	pessoa_fisica
where 	cd_pessoa_fisica	= cd_pessoa_fisica_p;

select 	obter_setor_usuario(nm_usuario_p)
into STRICT
  	cd_setor_atendimento_w
;

begin
	select	cd_estabelecimento
	into STRICT	cd_estabelecimento_w
	from	usuario
	where	nm_usuario = nm_usuario_p;
	exception
		when no_data_found then
			nm_usuario_w := upper(nm_usuario_p);
			select	cd_estabelecimento
			into STRICT	cd_estabelecimento_w
			from	usuario
			where	upper(nm_usuario) = nm_usuario_w;
end;



select	count(*)
into STRICT	qt_existe_cpi_w
from	same_cpi_prontuario
where	cd_pessoa_fisica	= cd_pessoa_fisica_p
and	nr_prontuario		= nr_prontuario_w
and 	cd_estabelecimento	= cd_estabelecimento_w;


if (qt_existe_cpi_w = 0) and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (nr_prontuario_w > 0) then

	select	nextval('same_cpi_prontuario_seq')
	into STRICT	nr_sequencia_w
	;

	insert into same_cpi_prontuario(
		nr_sequencia,
		cd_estabelecimento,
		dt_atualizacao,
		nm_usuario,
		nr_prontuario,
		cd_pessoa_fisica,
		qt_volume,
		ds_localizacao,
		dt_atualizacao_nrec,
		nm_usuario_nrec)
	values (	nr_sequencia_w,
		cd_estabelecimento_w,
		clock_timestamp(),
		nm_usuario_p,
		nr_prontuario_w,
		cd_pessoa_fisica_p,
		1,
		null,
		clock_timestamp(),
		nm_usuario_p);
end if;




select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_prontuario_w
from	same_cpi_prontuario
where	nr_prontuario		= nr_prontuario_w;


if (nr_seq_prontuario_w > 0) and (cd_estabelecimento_w > 0) and (cd_setor_atendimento_w > 0) and (nr_prontuario_w > 0) then
	begin

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_solic_w
	from	same_cpi_solic
	where	cd_estabelecimento		= cd_estabelecimento_w
	and	nr_prontuario			= nr_prontuario_w
	and	cd_setor_atendimento		= cd_setor_atendimento_w
	and	nr_seq_agenda_hc		= nr_seq_agenda_p
	and 	nr_seq_agenda_pac_hc		= nr_sequencia_p
	and	coalesce(dt_cancelamento::text, '') = ''
	and	trunc(dt_desejada, 'dd')	= trunc(dt_agenda_p, 'dd');

	if (nr_seq_solic_w = 0) then
		begin

		select	nextval('same_cpi_solic_seq')
		into STRICT	nr_seq_solic_w
		;

		insert 	into same_cpi_solic(nr_sequencia,
			cd_estabelecimento,
			nr_prontuario,
			dt_atualizacao,
			nm_usuario,
			nr_seq_agenda_hc,
			nr_seq_agenda_pac_hc,
			cd_setor_atendimento,
			dt_solicitacao,
			dt_desejada,
			ie_status_solic,
			nm_usuario_solic)
		values (nr_seq_solic_w,
			cd_estabelecimento_w,
			nr_prontuario_w,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_agenda_p,
			nr_sequencia_p,
			cd_setor_atendimento_w,
			clock_timestamp(),
			dt_agenda_p,
			'A',
			nm_usuario_p);
		end;
	end if;

	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
