CREATE OR REPLACE PROCEDURE hc_gravar_log_transferencia ( nr_seq_agenda_p bigint, dt_agenda_p timestamp, cd_pessoa_fisica_p text, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;
nr_seq_motivo_transf_w	bigint;
cd_perfil_w		integer;
cd_pessoa_fisica_w	varchar(15);


BEGIN

if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '')  then
	begin

	select	obter_perfil_ativo
	into STRICT	cd_perfil_w
	;

	if (cd_perfil_w = '') or (cd_perfil_w = 0) then
		cd_perfil_w := null;
	end if;

	select	max(nr_seq_motivo_transf)
	into STRICT	nr_seq_motivo_transf_w
	from	agenda_hc_paciente
	where	nr_sequencia = nr_seq_agenda_p;


	select	nextval('hc_transferencia_seq')
	into STRICT	nr_sequencia_w
	;

	insert into hc_transferencia(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		dt_transferencia,
		nr_seq_agenda_hc,
		dt_agenda,
		nr_seq_motivo_transf,
		cd_pessoa_fisica )
	values (	nr_sequencia_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nr_seq_agenda_p,
		dt_agenda_p,
		nr_seq_motivo_transf_w,
		cd_pessoa_fisica_p);
	end;
end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
