CREATE OR REPLACE PROCEDURE hc_insere_profissional_equipe (ds_itens_p text, nr_seq_paciente_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_pessoa_fisica_w	varchar(10);
ie_funcao_w		varchar(3);

C01 CURSOR FOR
	SELECT	cd_pessoa_fisica,
		ie_funcao
	from	hc_profissional
	where	obter_se_contido(nr_sequencia,ds_itens_p) = 'S';

BEGIN

open C01;
loop
fetch C01 into
	cd_pessoa_fisica_w,
	ie_funcao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if (ie_funcao_w IS NOT NULL AND ie_funcao_w::text <> '') then
		insert into paciente_hc_equipe(
			nr_sequencia,
			cd_pessoa_fisica,
			ie_funcao,
			dt_atualizacao,
			nm_usuario,
			nr_seq_paciente,
			dt_atualizacao_nrec,
			nm_usuario_nrec)
		values (	nextval('paciente_hc_equipe_seq'),
			cd_pessoa_fisica_w,
			ie_funcao_w,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_paciente_p,
			clock_timestamp(),
			nm_usuario_p);
	else
		CALL wheb_mensagem_pck.exibir_mensagem_abort(216275);
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
