CREATE OR REPLACE PROCEDURE hc_inserir_material_pad (cd_kit_material_p text, nr_seq_controle_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_kit_material_w	bigint;
cd_material_w		bigint;
qt_material_w		double precision;

C01 CURSOR FOR
	SELECT	cd_kit_material
	from	kit_material
	where	obter_se_contido(cd_kit_material,cd_kit_material_p) = 'S'
	order by cd_kit_material;

C02 CURSOR FOR
	SELECT	cd_material,
		qt_material
	from	componente_kit
	where	cd_kit_material = cd_kit_material_w;

BEGIN
if (cd_kit_material_p IS NOT NULL AND cd_kit_material_p::text <> '') then

	open C01;
	loop
	fetch C01 into
		cd_kit_material_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		open C02;
		loop
		fetch C02 into
			cd_material_w,
			qt_material_w;
		EXIT WHEN NOT FOUND; /* apply on C02 */
			begin
			insert into hc_pad_material(
				nr_sequencia,
				cd_kit_material,
				cd_material,
				dt_atualizacao,
				nm_usuario,
				nr_seq_controle,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				qt_material)
			values (	nextval('hc_pad_material_seq'),
				cd_kit_material_w,
				cd_material_w,
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_controle_p,
				clock_timestamp(),
				nm_usuario_p,
				qt_material_w);
			end;
		end loop;
		close C02;

		end;
	end loop;
	close C01;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
