CREATE OR REPLACE PROCEDURE hc_inserir_profissional_pad (nr_seq_controle_p bigint, ds_lista_profissional_p text, nr_sequencia_cronograma_p text, nm_usuario_p text) AS $body$
DECLARE


cd_procedimento_w		bigint;
ie_origem_proced_w		bigint;
cd_intervalo_w			varchar(7);

C01 CURSOR FOR
	SELECT	cd_procedimento,
		ie_origem_proced		
	from	hc_pad_procedimento
	where	nr_seq_profissional = ds_lista_profissional_p;
	
		
BEGIN				

			select	max(cd_intervalo)
			into STRICT	cd_intervalo_w
			from	hc_pad_prof_interv
			where	nr_sequencia = ds_lista_profissional_p;		
			
			open C01;
			loop
			fetch C01 into	
				cd_procedimento_w,
				ie_origem_proced_w;
			EXIT WHEN NOT FOUND; /* apply on C01 */
				begin
				
				insert into hc_pad_prof_proced(	
					nr_sequencia,
					cd_procedimento,
					dt_atualizacao,
					nm_usuario,
					nr_seq_profissional,
					ie_origem_proced,
					dt_atualizacao_nrec,
					nm_usuario_nrec)
				values (	nextval('hc_pad_prof_proced_seq'),
					cd_procedimento_w,
					clock_timestamp(),
					nm_usuario_p,
					nr_sequencia_cronograma_p,
					ie_origem_proced_w,
					clock_timestamp(),
					nm_usuario_p);
					
					commit;
				
			end;
	end loop;
close C01;	

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
