CREATE OR REPLACE PROCEDURE hc_replicar_regra_bloq_ag_cd (nr_seq_agenda_copia_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, nr_seq_bloqueio_p bigint, nr_seq_agenda_dest_p bigint) AS $body$
DECLARE


/* regras exame x bloqueio */

dt_inicial_w			timestamp;
dt_final_w			timestamp;
ie_motivo_bloqueio_w		varchar(1);
ds_observacao_w			varchar(255);
ie_dia_semana_w			integer;
hr_inicio_bloqueio_w		timestamp;
hr_final_bloqueio_w		timestamp;
nr_seq_bloqueio_w		bigint;

/* obter regras bloqueio */

c01 CURSOR FOR
SELECT	dt_inicial,
	dt_final,
	ie_motivo_bloqueio,
	ds_observacao,
	ie_dia_semana,
	hr_inicio_bloqueio,
	hr_final_bloqueio
from	hc_agenda_bloqueio
where	nr_seq_agenda = nr_seq_agenda_copia_p
and	nr_sequencia = nr_seq_bloqueio_p;


BEGIN
if (nr_seq_agenda_copia_p IS NOT NULL AND nr_seq_agenda_copia_p::text <> '') and (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (nr_seq_bloqueio_p IS NOT NULL AND nr_seq_bloqueio_p::text <> '') and (nr_seq_agenda_dest_p IS NOT NULL AND nr_seq_agenda_dest_p::text <> '') then

	/* gera regras */

	open c01;
	loop
	fetch c01 into	dt_inicial_w,
			dt_final_w,
			ie_motivo_bloqueio_w,
			ds_observacao_w,
			ie_dia_semana_w,
			hr_inicio_bloqueio_w,
			hr_final_bloqueio_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		select	nextval('hc_agenda_bloqueio_seq')
		into STRICT	nr_seq_bloqueio_w
		;

		insert into hc_agenda_bloqueio(
						nr_sequencia,
						nr_seq_agenda,
						dt_inicial,
						dt_final,
						ie_motivo_bloqueio,
						ds_observacao,
						ie_dia_semana,
						hr_inicio_bloqueio,
						hr_final_bloqueio,
						nm_usuario,
						dt_atualizacao
						)
				values (
						nr_seq_bloqueio_w,
						nr_seq_agenda_dest_p,
						dt_inicial_w,
						dt_final_w,
						ie_motivo_bloqueio_w,
						ds_observacao_w,
						ie_dia_semana_w,
						hr_inicio_bloqueio_w,
						hr_final_bloqueio_w,
						nm_usuario_p,
						clock_timestamp()
						);


		end;
	end loop;
	close c01;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
