CREATE OR REPLACE PROCEDURE hc_transferir_agendamento ( nm_usuario_p text, nr_seq_agenda_origem_p bigint, nr_seq_agenda_destino_p bigint, cd_pessoa_fisica_p text, ie_periodo_destino_p text, dt_inicial_p timestamp, dt_final_p timestamp, nr_motivo_transf_p bigint default null) AS $body$
DECLARE


nr_sequencia_w		bigint;
cd_profissional_w	varchar(10);
nr_seq_prof_hc_w	bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	agenda_hc_paciente
	where	nr_seq_agenda = nr_seq_agenda_origem_p
	and	((cd_pessoa_fisica = cd_pessoa_fisica_p) or (cd_pessoa_fisica_p = '0'))
	and	((ie_periodo_destino_p = 'S' and trunc(dt_agenda) >= trunc(clock_timestamp())) or (ie_periodo_destino_p = 'N' and trunc(dt_agenda) between trunc(dt_inicial_p) and trunc(dt_final_p)))
	and	ie_status_agenda = 'A'
	order by 1;

BEGIN

if (nr_seq_agenda_origem_p IS NOT NULL AND nr_seq_agenda_origem_p::text <> '') and (nr_seq_agenda_destino_p IS NOT NULL AND nr_seq_agenda_destino_p::text <> '') then


select	max(cd_pessoa_resp)
into STRICT	cd_profissional_w
from	agenda_home_care
where	nr_sequencia = nr_seq_agenda_destino_p;

select	max(nr_sequencia)
into STRICT	nr_seq_prof_hc_w
from	hc_profissional
where	cd_pessoa_fisica = cd_profissional_w
and		ie_situacao = 'A';

open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	update	agenda_hc_paciente
	set		nr_seq_agenda = nr_seq_agenda_destino_p,
			nr_seq_motivo_transf = nr_motivo_transf_p
	where	nr_sequencia = nr_sequencia_w;

	update	hc_agenda_prof
	set		nr_seq_prof_hc = nr_seq_prof_hc_w
	where	nr_seq_agenda = nr_sequencia_w;


	end;
end loop;
close C01;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
