SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE hc_transf_agendamento ( nr_seq_agenda_destino_p bigint, dt_agenda_p timestamp, nr_seq_agenda_hc_p bigint, nm_usuario_p text) AS $body$
DECLARE



cd_profissional_w	varchar(10);
nr_seq_prof_hc_w	bigint;


BEGIN

if (nr_seq_agenda_hc_p IS NOT NULL AND nr_seq_agenda_hc_p::text <> '') then
	begin
	select	max(cd_pessoa_resp)
	into STRICT	cd_profissional_w
	from	agenda_home_care
	where	nr_sequencia	= nr_seq_agenda_destino_p;

	select	max(nr_sequencia)
	into STRICT	nr_seq_prof_hc_w
	from	hc_profissional
	where	cd_pessoa_fisica = cd_profissional_w
	and	ie_situacao	= 'A';

	update	agenda_hc_paciente
	set	nr_seq_agenda	= nr_seq_agenda_destino_p,
		dt_agenda	= dt_agenda_p
	where	nr_sequencia	= nr_seq_agenda_hc_p;

	update	hc_agenda_prof
	set	nr_seq_prof_hc	= nr_seq_prof_hc_w
	where	nr_seq_agenda	= nr_seq_agenda_hc_p;


	end;
end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
