CREATE OR REPLACE PROCEDURE hc_valida_bloqueios_transf ( nm_usuario_p text, nr_seq_agenda_origem_p bigint, nr_seq_agenda_destino_p bigint, cd_pessoa_fisica_p text, ie_periodo_destino_p text, dt_inicial_p timestamp, dt_final_p timestamp, ds_erro_p INOUT text) AS $body$
DECLARE


nr_sequencia_w	bigint;
dt_agenda_w	timestamp;
ds_erro_w	varchar(255);

C01 CURSOR FOR
	SELECT	nr_sequencia,
		dt_agenda
	from	agenda_hc_paciente
	where	nr_seq_agenda = nr_seq_agenda_origem_p
	and	((cd_pessoa_fisica = cd_pessoa_fisica_p) or (cd_pessoa_fisica_p = '0'))
	and	((ie_periodo_destino_p = 'S' and trunc(dt_agenda) >= trunc(clock_timestamp())) or (ie_periodo_destino_p = 'N' and trunc(dt_agenda) between trunc(dt_inicial_p) and trunc(dt_final_p)))
	and	ie_status_agenda = 'A'
	order by 1;

BEGIN
ds_erro_w := null;

if (nr_seq_agenda_origem_p IS NOT NULL AND nr_seq_agenda_origem_p::text <> '') and (nr_seq_agenda_destino_p IS NOT NULL AND nr_seq_agenda_destino_p::text <> '') then

	open C01;
	loop
	fetch C01 into
		nr_sequencia_w,
		dt_agenda_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		if (coalesce(ds_erro_w,'x') = 'x') then
			select	hc_consulta_bloqueio(nr_seq_agenda_destino_p, dt_agenda_w, 'M')
			into STRICT	ds_erro_w
			;
		end if;

		end;
	end loop;
	close C01;

end if;

ds_erro_p := ds_erro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
