CREATE OR REPLACE PROCEDURE hdm_alterar_prog_equipe (nr_seq_captacao_p bigint, nr_seq_captacao_dest_p bigint, nr_seq_novo_programa_p bigint, nr_seq_equipe_p bigint, nm_usuario_p text, nr_seq_capt_dest_novo_p INOUT bigint) AS $body$
DECLARE


nr_seq_programa_w			bigint;


BEGIN

if (nr_seq_captacao_p IS NOT NULL AND nr_seq_captacao_p::text <> '') and (nr_seq_novo_programa_p IS NOT NULL AND nr_seq_novo_programa_p::text <> '') then

	if (nr_seq_captacao_dest_p IS NOT NULL AND nr_seq_captacao_dest_p::text <> '') then
		select	max(nr_seq_programa)
		into STRICT	nr_seq_programa_w
		from	mprev_captacao_destino
		where	nr_sequencia = nr_seq_captacao_dest_p;

		if (nr_seq_programa_w IS NOT NULL AND nr_seq_programa_w::text <> '') then
			update	mprev_captacao_destino
			set		dt_atualizacao = clock_timestamp(),
					nm_usuario = nm_usuario_p,
					nr_seq_programa = nr_seq_novo_programa_p,
					nr_seq_equipe = nr_seq_equipe_p
			where	nr_sequencia = nr_seq_captacao_dest_p;
		else
			insert into mprev_captacao_destino(
				nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_seq_captacao,
				nr_seq_programa,
				nr_seq_equipe)
			 values (
				nextval('mprev_captacao_destino_seq'),
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_captacao_p,
				nr_seq_novo_programa_p,
				nr_seq_equipe_p)
			returning nr_sequencia into nr_seq_capt_dest_novo_p;
		end if;
	end if;
end if;

commit;

end;
$body$
