CREATE OR REPLACE PROCEDURE hdm_desf_indic_capt_confirm ( nr_seq_indicacao_p mprev_indicacao_paciente.nr_sequencia%type, nr_seq_demand_espont_p mprev_demanda_espont.nr_sequencia%type, nr_seq_busca_empresa_p mprev_busca_empresarial.nr_sequencia%type, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Objetivo: Desfazer as confirmações de indicações de captação.
Goal: Undo acknowledgments of pickup indications.
-------------------------------------------------------------------------------------------------------------------

Direct Call Locations: HDM - Participant Control
[ ] Object Dictionary   [ x ] Tasy (Delphi/Java/HTML)   [ ] Web portal   [ ] Reports   [ ] Others:
 ------------------------------------------------------------------------------------------------------------------

Attention points:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
BEGIN

if (nr_seq_indicacao_p IS NOT NULL AND nr_seq_indicacao_p::text <> '') then
	update	mprev_indicacao_paciente
	set	dt_confirmacao  = NULL,
		nm_usuario_confirmacao  = NULL,
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where	nr_sequencia = nr_seq_indicacao_p;
	
	update	mprev_captacao
	set	dt_inclusao  = NULL,
		ie_status = 'N',
		nr_seq_forma_ingresso  = NULL,
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where	nr_seq_indicacao = nr_seq_indicacao_p;
	
	commit;
elsif (nr_seq_demand_espont_p IS NOT NULL AND nr_seq_demand_espont_p::text <> '') then
	update	mprev_demanda_espont
	set	dt_confirmacao  = NULL,
		nm_usuario_confirmacao  = NULL,
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where	nr_sequencia = nr_seq_demand_espont_p;
	
	update	mprev_captacao
	set	dt_inclusao  = NULL,
		ie_status = 'N',
		nr_seq_forma_ingresso  = NULL,
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where	nr_seq_demanda_espont = nr_seq_demand_espont_p;
	
	commit;
elsif (nr_seq_busca_empresa_p IS NOT NULL AND nr_seq_busca_empresa_p::text <> '') then
	update	mprev_busca_empresarial
	set	dt_confirmacao  = NULL,
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where	nr_sequencia = nr_seq_busca_empresa_p;

	update	mprev_captacao
	set	dt_inclusao  = NULL,
		ie_status = 'N',
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where	nr_seq_busca_emp = nr_seq_busca_empresa_p;
	
	commit;
end if;

-- The "commit" was commented to be done only when needed, ie within "if"

--commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
