CREATE OR REPLACE PROCEDURE hdm_desf_reprov_demand_espont ( nr_seq_demand_espont_p mprev_demanda_espont.nr_sequencia%type, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Objetivo: Desfazer a reprovação de uma demanda espontânea.
Goal: Undo a spontaneous demand disapproval.
-------------------------------------------------------------------------------------------------------------------

Direct Call Locations: HDM - Participant Control
[ ] Object Dictionary   [ x ] Tasy (Delphi/Java/HTML)   [ ] Web portal   [ ] Reports   [ ] Others:
 ------------------------------------------------------------------------------------------------------------------

Attention points:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
BEGIN

if (nr_seq_demand_espont_p IS NOT NULL AND nr_seq_demand_espont_p::text <> '') then
	update	mprev_demanda_espont
	set	dt_reprovacao  = NULL,
		nm_usuario_reprovacao  = NULL,
		ds_motivo_reprovacao  = NULL,
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where	nr_sequencia = nr_seq_demand_espont_p;
	
	commit;
end if;

-- The "commit" was commented to be done only when needed, ie within "if"

--commit;
end;
$body$
