SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE hdm_finalizar_prog_partic (nm_usuario_p text) AS $body$
DECLARE


nr_seq_participante_w	mprev_participante.nr_sequencia%type;
ie_contem_w		varchar(1) := 'N';


c01 CURSOR FOR
	SELECT distinct p.nr_sequencia
	from   mprev_participante p,
	       mprev_programa_partic t
	where  t.nr_seq_participante = p.nr_sequencia
	and    coalesce(t.dt_exclusao::text, '') = '';


BEGIN

open c01;
loop
fetch c01 into
	nr_seq_participante_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

		select	CASE WHEN coalesce(max(a.nr_sequencia),0)=0 THEN  'N'  ELSE 'S' END
		into STRICT	ie_contem_w
		from	mprev_prog_partic_modulo a,
			mprev_programa_partic b
		where	a.nr_seq_programa_partic = b.nr_sequencia
		and     b.nr_seq_participante = nr_seq_participante_w
		and (coalesce(dt_saida::text, '') = '' or dt_saida > clock_timestamp());

		if (ie_contem_w = 'N')then
			select  CASE WHEN coalesce(max(nr_sequencia),0)=0 THEN 'N'  ELSE 'S' END
			into STRICT	ie_contem_w
			from    mprev_campanha_partic
			where   nr_seq_participante = nr_seq_participante_w
			and (coalesce(dt_exclusao::text, '') = '' or dt_exclusao > clock_timestamp());

			if (ie_contem_w = 'N')then

				select  CASE WHEN coalesce(max(nr_sequencia),0)=0 THEN 'N'  ELSE 'S' END
				into STRICT	ie_contem_w
				from    mprev_grupo_turma_partic
				where   nr_seq_participante = nr_seq_participante_w
				and (coalesce(dt_saida::text, '') = '' or dt_saida > clock_timestamp());

				if (ie_contem_w = 'N')then

					select  CASE WHEN coalesce(max(nr_sequencia),0)=0 THEN 'N'  ELSE 'S' END
					into STRICT	ie_contem_w
					from    mprev_agendamento
					where   nr_seq_participante = nr_seq_participante_w
					and	ie_status_agenda in ('A','CN');

					if (ie_contem_w = 'N')then

						update	mprev_programa_partic
						set	dt_exclusao    	= clock_timestamp(),
							dt_atualizacao 	= clock_timestamp(),
							nm_usuario	= nm_usuario_p
						where	nr_seq_participante = nr_seq_participante_w;

					end if;
				end if;
			end if;
		end if;
	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
