CREATE OR REPLACE PROCEDURE hdm_vincular_ciclo_agendamento ( nr_seq_agenda_int_p agenda_integrada.nr_sequencia%type, nr_seq_partic_ciclo_item_p mprev_partic_ciclo_item.nr_sequencia%type, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Goal: Update the feature "Integrated Schedule" to linking or unlinking the scheduling from agenda with to HDM.
-------------------------------------------------------------------------------------------------------------------

Direct Call Locations: HDM - Participant Control
[ ] Object Dictionary   [ x ] Tasy (Delphi/Java/HTML)   [ ] Web portal   [ ] Reports   [ ] Others:
 ------------------------------------------------------------------------------------------------------------------

Attention points:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_partic_ciclo_item_w	mprev_partic_ciclo_item.nr_sequencia%type;


BEGIN

if (nr_seq_agenda_int_p IS NOT NULL AND nr_seq_agenda_int_p::text <> '' AND nr_seq_partic_ciclo_item_p IS NOT NULL AND nr_seq_partic_ciclo_item_p::text <> '') then
	if (nr_seq_partic_ciclo_item_p = 0) then
		nr_seq_partic_ciclo_item_w := null;
	else
		nr_seq_partic_ciclo_item_w := nr_seq_partic_ciclo_item_p;
	end if;

	update	agenda_integrada
	set		nr_seq_partic_ciclo_item = nr_seq_partic_ciclo_item_w,
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
	where	nr_sequencia = nr_seq_agenda_int_p;
	
	commit;
end if;

-- The "commit" was commented to be done only when needed, ie within "if"

--commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
