SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE hd_ajustar_dialisador_pac (cd_pessoa_origem_p text, cd_pessoa_destino_p text, ie_desab_trigger_p text) AS $body$
DECLARE


qt_dialisador_origem_w	integer;
qt_dialisador_destino_w	integer;
qt_dialisador_w		integer;
nr_seq_dialisador_w	bigint;
qt_primeira_ordem_w	bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	hd_dializador
	where	cd_pessoa_fisica = cd_pessoa_origem_p
	
union all

	SELECT	nr_sequencia
	from	hd_dializador
	where	cd_pessoa_fisica = cd_pessoa_destino_p
	order by nr_sequencia desc;

BEGIN
if (ie_desab_trigger_p = 'S') then
	CALL exec_sql_dinamico('ACDUPLICPF','alter trigger HD_DIALIZADOR_BEFORE_UPDATE disable');
end if;

select	count(*)
into STRICT	qt_dialisador_origem_w
from	hd_dializador
where	cd_pessoa_fisica = cd_pessoa_origem_p;

select	count(*)
into STRICT	qt_dialisador_destino_w
from	hd_dializador
where	cd_pessoa_fisica = cd_pessoa_destino_p;

qt_dialisador_w := qt_dialisador_origem_w + qt_dialisador_destino_w;
qt_primeira_ordem_w := 999999;

if (qt_dialisador_origem_w > 0) then
	open C01;
	loop
	fetch C01 into
		nr_seq_dialisador_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		update	hd_dializador
		set	nr_capilar_prc = qt_primeira_ordem_w
		where	nr_sequencia   = nr_seq_dialisador_w;

		if (qt_primeira_ordem_w > 0) then
			qt_primeira_ordem_w := qt_primeira_ordem_w - 1;
		end if;

		end;
	end loop;
	close C01;

	open C01;
	loop
	fetch C01 into
		nr_seq_dialisador_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		update	hd_dializador
		set	nr_capilar_prc   = qt_dialisador_w,
			cd_pessoa_fisica = cd_pessoa_destino_p
		where	nr_sequencia     = nr_seq_dialisador_w;

		if (qt_dialisador_w > 0) then
			qt_dialisador_w := qt_dialisador_w - 1;
		end if;
		end;
	end loop;
	close C01;

end if;

if (ie_desab_trigger_p = 'S') then
	CALL exec_sql_dinamico('ACDUPLICPF','alter trigger HD_DIALIZADOR_BEFORE_UPDATE enable');
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
