CREATE OR REPLACE PROCEDURE hd_ajustar_dialisador_pf_dup ( cd_pessoa_origem_p text, cd_pessoa_destino_p text) AS $body$
DECLARE


nr_seq_dialisador_w		bigint;
nr_capilar_prc_w			bigint;
count_w					bigint;
nr_capilar_prox_w		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
			NR_CAPILAR_PRC
	from	hd_dializador
	where	cd_pessoa_fisica = cd_pessoa_origem_p
	order by nr_capilar_prc;


BEGIN
open C01;
loop
fetch C01 into
	nr_seq_dialisador_w,
	nr_capilar_prc_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	count(*)
	into STRICT	count_w
	from	hd_dializador
	where	cd_pessoa_fisica = cd_pessoa_destino_p
	and		NR_CAPILAR_PRC = nr_capilar_prc_w;

	if (count_w > 0) then
		select	max(nr_capilar_prc) + 1
		into STRICT	nr_capilar_prox_w
		from	hd_dializador
		where	cd_pessoa_fisica = cd_pessoa_origem_p
		or		cd_pessoa_fisica = cd_pessoa_destino_p;

		update	hd_dializador
		set		cd_pessoa_fisica = cd_pessoa_destino_p,
				nr_capilar_prc = nr_capilar_prox_w
		where	nr_sequencia = nr_seq_dialisador_w;

	else
		update	hd_dializador
		set		cd_pessoa_fisica = cd_pessoa_destino_p
		where	nr_sequencia = nr_seq_dialisador_w;
	end if;


	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
