SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE hd_ajusta_tratamento () AS $body$
DECLARE



cd_pessoa_fisica_w	varchar(10);
dt_inicio_w		timestamp;
dt_fim_w		timestamp;
ds_motivo_fim_w		varchar(255);
nm_usuario_w		varchar(15);
qt_registro_w		bigint;

c01 CURSOR FOR
	SELECT	cd_pessoa_fisica,
		dt_inicio,
		dt_fim,
		ds_motivo_fim,
		nm_usuario
	from	hd_pac_renal_cronico
	where	(dt_inicio IS NOT NULL AND dt_inicio::text <> '')
	order by cd_pessoa_fisica;


BEGIN

open c01;
loop
fetch c01 into
	cd_pessoa_fisica_w,
	dt_inicio_w,
	dt_fim_w,
	ds_motivo_fim_w,
	nm_usuario_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	
	select	count(*)
	into STRICT	qt_registro_w
	from	paciente_tratamento
	where	cd_pessoa_fisica	= cd_pessoa_fisica_w
	and	ie_tratamento in ('HD','CH','CHD','CHDF','CHDI','PAF','SCUF','SLED','DDC','IHF','IHDF');
	
	if (qt_registro_w = 0) then
	
		insert into paciente_tratamento(
			nr_sequencia,
			cd_pessoa_fisica,
			ie_tratamento,
			dt_inicio_tratamento,
			dt_final_tratamento,
			ds_motivo_fim,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec
		) values (
			nextval('paciente_tratamento_seq'),
			cd_pessoa_fisica_w,
			'HD',
			dt_inicio_w,
			dt_fim_w,
			ds_motivo_fim_w,
			clock_timestamp(),
			nm_usuario_w,
			clock_timestamp(),
			nm_usuario_w
		);
		
	end if;
	
	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
