CREATE OR REPLACE PROCEDURE hd_alterar_avaliacao_unidade ( nr_sequencia_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
BEGIN

if (ie_opcao_p = 'L') then

	update	hd_avaliacao_unidade
	set 	dt_liberacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where 	nr_sequencia = nr_sequencia_p;

elsif (ie_opcao_p = 'I') then

	update	hd_avaliacao_unidade
	set 	ie_situacao = 'I',
		nm_usuario = nm_usuario_p
	where 	nr_sequencia = nr_sequencia_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
