SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE hd_alterar_reprocessador ( nr_seq_reprocessamento_p bigint, nr_seq_reprocessador_p bigint, ds_retorno_p INOUT text) AS $body$
DECLARE


nr_seq_dialisador_w	bigint;
nr_seq_reproc_w		bigint;
qt_min_reuso_w		bigint;
qt_registro_w		bigint;
nr_dialisador_w		bigint;


BEGIN

/* Selecionar o dialisador */

select	nr_seq_dialisador,
	nr_seq_reproc
into STRICT	nr_seq_dialisador_w,
	nr_seq_reproc_w
from	hd_dialisador_reproc
where	nr_sequencia	= nr_seq_reprocessamento_p
and	coalesce(ie_situacao,'S') <> 'I';

/* Se forem iguais não faz nada  */

if (nr_seq_reproc_w = nr_seq_reprocessador_p) then
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(264147);
else
	begin

	/* Atualiza o reprocessador */

	update	hd_dialisador_reproc
	set	nr_seq_reproc		= nr_seq_reprocessador_p
	where	nr_sequencia		= nr_seq_reprocessamento_p;

	/* Verifica informação do reuso máximo */

	select	min(b.nr_max_reuso)
	into STRICT	qt_min_reuso_w
	from	hd_reproc_dializador b,
		hd_dialisador_reproc a
	where	a.nr_seq_reproc		= b.nr_sequencia
	and	a.nr_seq_dialisador	= nr_seq_dialisador_w
	and	coalesce(a.ie_situacao,'S') <> 'I';

	select	count(*)
	into STRICT	qt_registro_w
	from	hd_dializador
	where	nr_sequencia		= nr_seq_dialisador_w
	and	nr_max_reuso 		<> qt_min_reuso_w;

	if (qt_registro_w > 0) then

		/* Atualiza reuso máximo do dialisador */

		update	hd_dializador
		set	nr_max_reuso	= qt_min_reuso_w
		where	nr_sequencia	= nr_seq_dialisador_w;

		/* Pega o número do dialisador para mostrar na tela */

		select	nr_dializador
		into STRICT	nr_dialisador_w
		from	hd_dializador
		where	nr_sequencia	= nr_seq_dialisador_w;

		ds_retorno_p	:= wheb_mensagem_pck.get_texto(795802,
							'NR_DIALISADOR='||nr_dialisador_w||
							';QT_MIN_REUSO='||qt_min_reuso_w);
	else
		ds_retorno_p	:= wheb_mensagem_pck.get_texto(795818);
	end if;

	/* Comita alterações */

	commit;

	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE hd_alterar_reprocessador ( nr_seq_reprocessamento_p bigint, nr_seq_reprocessador_p bigint, ds_retorno_p INOUT text) FROM PUBLIC;

