SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE hd_altera_regra_solic () AS $body$
DECLARE

ie_regra_ok_w		varchar(1);
ie_regra_solic_w	varchar(15);
cd_pessoa_fisica_w	varchar(10);
cd_pessoa_exame_w	varchar(10);
nr_seq_prot_exame_w	bigint;
ie_diabetico_w		varchar(1);
ie_regra_solic_novo_w	varchar(15);
C01 CURSOR FOR
	SELECT	cd_pessoa_fisica
	from	hd_pac_renal_cronico
	where	hd_obter_se_paciente_ativo(cd_pessoa_fisica) = 'S';

C02 CURSOR FOR
	SELECT	ie_regra_solic,
		cd_pessoa_fisica,
		nr_sequencia
	from	hd_pac_prot_exame
	where	cd_pessoa_fisica =  cd_pessoa_fisica_w;

BEGIN
open C01;
loop
fetch C01 into
	cd_pessoa_fisica_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	open C02;
	loop
	fetch C02 into
		ie_regra_solic_w,
		cd_pessoa_exame_w,
		nr_seq_prot_exame_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		if (ie_regra_solic_w IS NOT NULL AND ie_regra_solic_w::text <> '') then
			ie_regra_ok_w := hd_obter_se_regra_exame(ie_regra_solic_w,cd_pessoa_exame_w,'S');
			if (ie_regra_ok_w = 'N') then
				ie_regra_solic_novo_w := hd_obter_regra_solic(cd_pessoa_exame_w);
				if (ie_regra_solic_novo_w IS NOT NULL AND ie_regra_solic_novo_w::text <> '') then
					update	hd_pac_prot_exame
					set	ie_regra_solic = ie_regra_solic_novo_w
					where	nr_sequencia = nr_seq_prot_exame_w;

					--gravar_log_tasy(65874,'Sequencia='||nr_seq_prot_exame_w||' Regra antiga='||ie_regra_solic_w||' Regra nova='||ie_regra_solic_novo_w,'tasy');
				end if;
			end if;
		end if;
		end;
	end loop;
	close C02;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE hd_altera_regra_solic () FROM PUBLIC;

