CREATE OR REPLACE PROCEDURE hd_atualizar_conferido ( nr_prescricao_p bigint, ie_conferir_p text default 'S', nm_usuario_p text DEFAULT NULL) AS $body$
DECLARE


nr_seq_prescr_conferida		hd_prescr_conferida.nr_sequencia%type;

BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then

	select	max(nr_sequencia)
	into STRICT	nr_seq_prescr_conferida
	from	hd_prescr_conferida
	where 	nr_prescricao = nr_prescricao_p;

	if	((coalesce(nr_seq_prescr_conferida::text, '') = '') or (nr_seq_prescr_conferida <= 0)) and (ie_conferir_p = 'S') then

		insert into hd_prescr_conferida(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_prescricao,
			ie_conferido)
		values (
			nextval('hd_prescr_conferida_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_prescricao_p,
			ie_conferir_p);
	else
		update	hd_prescr_conferida c
		set	ie_conferido 	= ie_conferir_p,
			nm_usuario	= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
		where 	c.nr_prescricao = nr_prescricao_p;
	end if;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
