CREATE OR REPLACE PROCEDURE hd_atualizar_nr_hemodialise ( cd_pessoa_fisica_p text, nr_seq_paciente_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;
nr_seq_pac_w		bigint;
qt_hemodialise_w	integer;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_paciente
	from	hd_dialise
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	nr_seq_paciente > nr_seq_paciente_p
	order by nr_seq_paciente;


BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (nr_seq_paciente_p IS NOT NULL AND nr_seq_paciente_p::text <> '') then

	select	count(*)
	into STRICT	qt_hemodialise_w
	from	hd_dialise
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	nr_seq_paciente = nr_seq_paciente_p;

	if (qt_hemodialise_w = 0) then

		open C01;
		loop
		fetch C01 into
			nr_sequencia_w,
			nr_seq_pac_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin

			nr_seq_pac_w := nr_seq_pac_w-1;

			update	hd_dialise
			set	nr_seq_paciente = nr_seq_pac_w
			where	nr_sequencia = nr_sequencia_w;

			end;
		end loop;
		close C01;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
