SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE hd_avisa_alteracao_medic ( cd_pessoa_fisica_p text, cd_material_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
cd_perfil_w		integer;
ds_titulo_w		varchar(255);
ds_comunicado_w		varchar(2000);


BEGIN 
 
/* Pega o perfil que deverá receber a comunicação */
 
select	coalesce(max(cd_perfil_laudo),0) 
into STRICT	cd_perfil_w 
from	hd_parametro 
where	cd_estabelecimento	= cd_estabelecimento_p;
 
if (cd_perfil_w = 0) then 
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(264187);
end if;
 
/* Monta mensagem */
 
ds_titulo_w		:= wheb_mensagem_pck.get_texto(795799, 
						'CD_PESSOA_FISICA='||cd_pessoa_fisica_p|| 
						';NM_PESSOA_FISICA='||substr(obter_nome_pf(cd_pessoa_fisica_p),1,80));
				 
ds_comunicado_w		:= wheb_mensagem_pck.get_texto(795801, 
						'NM_PESSOA_FISICA='||substr(obter_nome_pf(cd_pessoa_fisica_p),1,80)|| 
						';CD_MATERIAL_P='||cd_material_p|| 
						';DS_MATERIAL='||substr(obter_desc_material(cd_material_p),1,80)) || chr(10) || 
 			wheb_mensagem_pck.get_texto(795798) || ' ' || SUBSTR(HD_OBTER_DESC_UNID_DIALISE(HD_OBTER_UNIDADE_ESTAB(cd_estabelecimento_p)),1,90);
 
insert into comunic_interna( 
	dt_comunicado, 
	ds_titulo, 
	ds_comunicado, 
	nm_usuario, 
	dt_atualizacao, 
	ie_geral, 
	nm_usuario_destino, 
	ds_perfil_adicional, 
	nr_sequencia, 
	ie_gerencial, 
	dt_liberacao, 
	cd_estab_destino 
) values ( 
	clock_timestamp(), 
	ds_titulo_w, 
	ds_comunicado_w, 
	nm_usuario_p, 
	clock_timestamp(), 
	'N', 
	'', 
	to_char(cd_perfil_w)||', ', 
	nextval('comunic_interna_seq'), 
	'N', 
	clock_timestamp(), 
	cd_estabelecimento_p 
);
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
