SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE hd_baca_ajustar_observacao (nm_usuario_p text) AS $body$
DECLARE



nr_seq_dialise_w		bigint;
nr_seq_tratamento_w		bigint;

c01 CURSOR FOR
SELECT 	nr_sequencia
from	hd_dialise
where	(ds_motivo_cancelamento IS NOT NULL AND ds_motivo_cancelamento::text <> '');


c02 CURSOR FOR
SELECT 	nr_sequencia
from	paciente_tratamento
where	(ds_motivo_fim IS NOT NULL AND ds_motivo_fim::text <> '');


BEGIN

OPEN C01;
LOOP
FETCH C01 into
	nr_seq_dialise_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	update 	hd_dialise
	set 	ds_observacao = ds_motivo_cancelamento
	where 	nr_sequencia = nr_Seq_dialise_w;

	end;
END LOOP;
CLOSE C01;


OPEN C02;
LOOP
FETCH C02 into
	nr_seq_tratamento_w;
EXIT WHEN NOT FOUND; /* apply on c02 */
	begin

	update 	paciente_tratamento
	set 	ds_observacao = ds_motivo_fim
	where 	nr_sequencia = nr_seq_tratamento_w;

	end;
END LOOP;
CLOSE C02;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
