CREATE OR REPLACE PROCEDURE hd_confirma_paciente (cd_pessoa_fisica_p text, nm_usuario_p text, ie_status_p text) AS $body$
DECLARE


/* ie_status
	CO - Confirmado
  N - NÃ£o Confirmado
*/
ie_status_w  varchar(10);


BEGIN

  select max(ie_status)
    into STRICT ie_status_w
    from hd_status_recepcao
   where cd_pessoa_fisica = cd_pessoa_fisica_p
     and ie_situacao = 'A'
	 and	trunc(dt_status) = trunc(clock_timestamp());

  if (coalesce(ie_status_w::text, '') = '') then
     insert into HD_STATUS_RECEPCAO(
                  nr_sequencia,
                  dt_atualizacao,
                  nm_usuario,
                  dt_atualizacao_nrec,
                  nm_usuario_nrec,
                  cd_pessoa_fisica,
                  ie_status,
                  dt_status,
                  ie_situacao
                  ) values (
                  nextval('hd_status_recepcao_seq'),
                  clock_timestamp(),
                  nm_usuario_p,
                  clock_timestamp(),
                  nm_usuario_p,
                  cd_pessoa_fisica_p,
                  ie_status_p,
                  clock_timestamp(),
                  'A'
                );
  end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
