CREATE OR REPLACE PROCEDURE hd_consiste_modelo_dialisador (nr_seq_modelo_p bigint, cd_pessoa_fisica_p text, nm_usuario_p text) AS $body$
DECLARE

qt_peso_minimo_w	bigint;
qt_peso_maximo_w	bigint;
qt_peso_ideal_w		real;


BEGIN

select 	max(qt_peso_minimo),
	max(qt_peso_maximo)
into STRICT	qt_peso_minimo_w,
	qt_peso_maximo_w
from	hd_modelo_dialisador
where	nr_sequencia = nr_seq_modelo_p;

if (qt_peso_minimo_w IS NOT NULL AND qt_peso_minimo_w::text <> '') and (qt_peso_maximo_w IS NOT NULL AND qt_peso_maximo_w::text <> '') then

	select 	coalesce(max(qt_peso_ideal),0)
	into STRICT	qt_peso_ideal_w
	from	hd_pac_renal_cronico
	where	cd_pessoa_fisica = cd_pessoa_fisica_p;

	if (qt_peso_ideal_w > 0) and
		((qt_peso_ideal_w < qt_peso_minimo_w) or (qt_peso_ideal_w > qt_peso_maximo_w)) then

		CALL Wheb_mensagem_pck.exibir_mensagem_abort( 264182 , 'QT_PESO_IDEAL_W='||qt_peso_ideal_w);
	end if;


end if;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
