SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE hd_consistir_cad_dialisador ( cd_material_p bigint, cd_pessoa_fisica_p text, cd_perfil_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
cd_local_estoque_w		smallint;
cd_estab_w			integer;
nr_atendimento_w		bigint;

ie_consignado_w			varchar(1);
cd_material_estoque_w		integer;
qt_conv_estoque_consumo_w	double precision;
cd_unidade_medida_estoque_w	varchar(30);
cd_unidade_medida_consumo_w	varchar(30);
qt_estoque_w			double precision;

/*Par√¢metros*/
 
ie_incluir_sem_saldo_estoque_w	varchar(1) := coalesce(obter_valor_param_usuario(7009, 177, cd_perfil_p, nm_usuario_p, cd_estabelecimento_p),'S');
ie_local_estoque_w		varchar(1) := coalesce(obter_valor_param_usuario(7009, 258, Obter_perfil_Ativo, wheb_usuario_pck.get_nm_usuario, 0),'U');


BEGIN 
 
if (ie_incluir_sem_saldo_estoque_w = 'N') and (cd_material_p IS NOT NULL AND cd_material_p::text <> '') and (cd_material_p > 0) then 
 
	select 	max(nr_atendimento) 
	into STRICT	nr_atendimento_w 
	from  	atendimento_paciente a 
	where 	cd_pessoa_fisica = cd_pessoa_fisica_p 
	and  	cd_estabelecimento = cd_estabelecimento_p 
	and	ie_fim_conta <> 'F' 
	and	ie_fim_conta <> 'F' 
	and	exists (SELECT 1 from HD_LOG_GERACAO_ATEND x where a.nr_atendimento = x.NR_ATENDIMENTO_NOV) 
	and	coalesce(dt_alta::text, '') = '';
								 
 
	if (coalesce(nr_atendimento_w,0) = 0) then 
		select 	max(nr_atendimento) 
		into STRICT	nr_atendimento_w 
		from  	atendimento_paciente 
		where 	cd_pessoa_fisica = cd_pessoa_fisica_p 
		and  	cd_estabelecimento = cd_estabelecimento_p 
		and	ie_fim_conta <> 'F' 
		and	coalesce(dt_alta::text, '') = '';
	end if;
	 
	if (coalesce(nr_atendimento_w,0) = 0) then 
		CALL wheb_mensagem_pck.exibir_mensagem_abort(264214);
	end if;
 
	select 	max(cd_estabelecimento) 
	into STRICT	cd_estab_w 
	from	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_w;
	 
	if (ie_local_estoque_w = 'U') then 
		 
		select	max(cd_local_estoque) 
		into STRICT	cd_local_estoque_w 
		from	setor_atendimento 
		where	cd_setor_atendimento = wheb_usuario_pck.get_cd_setor_atendimento;
		 
	elsif (ie_local_estoque_w = 'P') then 
		 
		select	obter_local_estoque_setor(Obter_Setor_Atendimento(nr_atendimento_w), obter_estab_atendimento(nr_atendimento_w)) 
		into STRICT	cd_local_estoque_w 
		;
		 
	end if;	
 
	select	coalesce(max(ie_consignado),'0'), 
		max(cd_material_estoque), 
		max(qt_conv_estoque_consumo), 
		max(substr(obter_dados_material_estab(cd_material,cd_estab_w,'UME'),1,30)) cd_unidade_medida_estoque, 
		max(substr(obter_dados_material_estab(cd_material,cd_estab_w,'UMS'),1,30)) cd_unidade_medida_consumo 
	into STRICT	ie_consignado_w, 
		cd_material_estoque_w, 
		qt_conv_estoque_consumo_w, 
		cd_unidade_medida_estoque_w, 
		cd_unidade_medida_consumo_w 
	from	material 
	where	cd_material = cd_material_p;	
 
	if (coalesce(cd_local_estoque_w, 0) > 0) then 
 
		qt_estoque_w	:= obter_saldo_disp_estoque(cd_estab_w,cd_material_estoque_w,cd_local_estoque_w,trunc(clock_timestamp(),'mm'));
		 
		if (cd_unidade_medida_consumo_w <> cd_unidade_medida_estoque_w) then 
			qt_estoque_w := (qt_estoque_w * qt_conv_estoque_consumo_w);
		end if;									
			 
		if (qt_estoque_w < 1) then 
			CALL Wheb_mensagem_pck.exibir_mensagem_abort(264220, 'cd_material_p=' || cd_material_p || ';qt_estoque_w=' || to_char(qt_estoque_w) || ';cd_local_estoque_w=' || substr(OBTER_DESC_LOCAL_ESTOQUE(cd_local_estoque_w),1,100));				
		end if;
 
	end if;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
