CREATE OR REPLACE PROCEDURE hd_consistir_data_fim_trat ( dt_inicio_tratamento_p timestamp, dt_final_tratamento_p timestamp, nm_usuario_p text) AS $body$
BEGIN

if (dt_final_tratamento_p IS NOT NULL AND dt_final_tratamento_p::text <> '') and (pkg_date_utils.START_OF(dt_final_tratamento_p, 'dd') < pkg_date_utils.START_OF(dt_inicio_tratamento_p, 'dd')) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(840851); /*A data final nÃ£o pode ser menor que a data inicial do tratamento.*/
end if;

end;
$body$
