CREATE OR REPLACE PROCEDURE hd_desfazer_falta_paciente (cd_pessoa_fisica_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

				
nr_seq_dialise_w		bigint;
nr_seq_dialise_atual_w	bigint;


BEGIN

select max(nr_sequencia)
into STRICT nr_seq_dialise_w
from hd_prc_chegada 
where cd_pessoa_fisica = cd_pessoa_fisica_p 
and ie_pac_faltou = 'S'
and cd_estabelecimento = cd_estabelecimento_p;

if (nr_seq_dialise_w IS NOT NULL AND nr_seq_dialise_w::text <> '') then	
	delete 	
	from 	hd_prc_chegada
	where nr_sequencia = nr_seq_dialise_w
	and	cd_pessoa_fisica	= cd_pessoa_fisica_p;
	
end if;

nr_seq_dialise_atual_w := hd_obter_dialise_atual(cd_pessoa_fisica_p,'I');

if (coalesce(nr_seq_dialise_atual_w::text, '') = '') then
			select  max(nr_sequencia)
      into STRICT nr_seq_dialise_atual_w
      from   hd_dialise
      where 	cd_pessoa_fisica 	= cd_pessoa_fisica_p 
      and 	coalesce(dt_fim_dialise::text, '') = '';
end if;

if (nr_seq_dialise_atual_w IS NOT NULL AND nr_seq_dialise_atual_w::text <> '') then
  update	hd_dialise
  set	dt_cancelamento  = NULL,
    nr_seq_motivo_fim  = NULL,
    ds_observacao  = NULL,
    cd_pf_cancelamento  = NULL
  where	nr_sequencia = 	nr_seq_dialise_atual_w;
end if;

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
