CREATE OR REPLACE PROCEDURE hd_encerrar_escala_trat_pac (CD_SEQ_PACIENTE_P text) AS $body$
DECLARE


    c01 CURSOR(cd_seq_pacientes_p text) FOR
        SELECT hed.nr_sequencia
          FROM hd_paciente_int_ext pie,
               hd_escala_dialise   hed
         WHERE pie.cd_pessoa_fisica = hed.cd_pessoa_fisica
           AND coalesce(hed.dt_fim::text, '') = ''
           AND obter_se_contido(pie.nr_sequencia, cd_seq_pacientes_p) = 'S';

BEGIN
    FOR i IN c01(cd_seq_pacientes_p => cd_seq_paciente_p) LOOP
        CALL hd_encerrar_escala_tratamento(i.nr_sequencia);
    END LOOP;

    COMMIT;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
